mapper.select:
select

from nideshop_order


        and id = #{id,jdbcType=BIGINT}


        and order_sn = #{orderSn,jdbcType=VARCHAR}


        and user_id = #{userId,jdbcType=BIGINT}


        and username = #{username,jdbcType=VARCHAR}


        and order_status = #{orderStatus,jdbcType=INTEGER}


        and shipping_status = #{shippingStatus,jdbcType=BOOLEAN}


        and pay_status = #{payStatus,jdbcType=BOOLEAN}


        and consignee = #{consignee,jdbcType=VARCHAR}


        and country = #{country,jdbcType=VARCHAR}


        and province = #{province,jdbcType=VARCHAR}


        and city = #{city,jdbcType=VARCHAR}


        and district = #{district,jdbcType=VARCHAR}


        and address = #{address,jdbcType=VARCHAR}


        and lng_lat = #{lngLat,jdbcType=VARCHAR}


        and mobile = #{mobile,jdbcType=VARCHAR}


        and postscript = #{postscript,jdbcType=VARCHAR}


        and shipping_id = #{shippingId,jdbcType=BIGINT}


        and shipping_name = #{shippingName,jdbcType=VARCHAR}


        and pay_id = #{payId,jdbcType=VARCHAR}


        and pay_name = #{payName,jdbcType=VARCHAR}


        and shipping_fee = #{shippingFee,jdbcType=DECIMAL}


        and actual_price = #{actualPrice,jdbcType=DECIMAL}


        and integral = #{integral,jdbcType=BIGINT}


        and integral_money = #{integralMoney,jdbcType=BIGINT}


        and order_price = #{orderPrice,jdbcType=DECIMAL}


        and goods_price = #{goodsPrice,jdbcType=DECIMAL}


        and agent_price = #{agentPrice,jdbcType=DECIMAL}


        and add_time = #{addTime,jdbcType=TIMESTAMP}


        and confirm_time = #{confirmTime,jdbcType=TIMESTAMP}


        and pay_time = #{payTime,jdbcType=TIMESTAMP}


        and pay_type = #{payType,jdbcType=VARCHAR}


        and freight_price = #{freightPrice,jdbcType=DECIMAL}


        and user_coupon_id = #{userCouponId,jdbcType=BIGINT}


        and coupon_name = #{couponName,jdbcType=VARCHAR}


        and parent_id = #{parentId,jdbcType=BIGINT}


        and coupon_price = #{couponPrice,jdbcType=DECIMAL}


        and callback_status = #{callbackStatus,jdbcType=OTHER}


        and shipping_no = #{shippingNo,jdbcType=VARCHAR}


        and full_cut_price = #{fullCutPrice,jdbcType=DECIMAL}


        and order_type = #{orderType,jdbcType=VARCHAR}


        and shipping_type = #{shippingType,jdbcType=VARCHAR}


        and pick_up_code = #{pickUpCode,jdbcType=VARCHAR}


        and pick_up_code_used = #{pickUpCodeUsed,jdbcType=VARCHAR}


        and print_ticket_flag = #{printTicketFlag,jdbcType=BOOLEAN}


        and creator = #{creator,jdbcType=VARCHAR}


        and create_name = #{createName,jdbcType=VARCHAR}


        and modifier = #{modifier,jdbcType=VARCHAR}


        and modify_name = #{modifyName,jdbcType=VARCHAR}


        and modify_time = #{modifyTime,jdbcType=TIMESTAMP}


        and `status` = #{status,jdbcType=BOOLEAN}


        and rec_status = #{recStatus,jdbcType=BOOLEAN}


        and statistics_status = #{statisticsStatus,jdbcType=BOOLEAN}


        and store_id = #{storeId,jdbcType=INTEGER}


        and pick_up_code_url = #{pickUpCodeUrl,jdbcType=VARCHAR}


        and weight_num = #{weightNum,jdbcType=BIGINT}


        and print_status = #{printStatus,jdbcType=BOOLEAN}




        and null = #{createBy}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and null = #{updateBy}


        and null = #{updateTime}


        and remark = #{remark,jdbcType=VARCHAR}
